package addition;

public class exp7cj {
	

		/**
		 * @param args
		 * overloading
		 */
		 void sum(int a,int b){System.out.println(a+b);}  
		  void sum(int a,int b,int c){System.out.println(a+b+c);}  
		public static void main(String[] args) {
			// TODO Auto-generated method stub
			 exp7cj obj=new exp7cj();  
			  obj.sum(10,10,10);  
			  obj.sum(20,20);  
		}

	}


